-- 场次
CREATE TABLE tours(
    tid INT PRIMARY KEY AUTO_INCREMENT,
    -- 关联现场 1~44
    vid INT, 
    -- 关联演出 1~20
    sid INT, 
    -- 总票数
    count INT,
    -- 价格
    price DECIMAL,
    -- 开始时间
    time BIGINT,
    -- 结束时间
    endtime BIGINT,
    FOREIGN KEY (vid) REFERENCES venues(vid),
    FOREIGN KEY (sid) REFERENCES shows(sid)
);
-- 1~300




-- 想看演出的人
CREATE TABLE wants(
    wid INT PRIMARY KEY AUTO_INCREMENT,
    -- 关联场次
    tid INT, 
    -- 关联用户
    uid  INT,
    FOREIGN KEY (tid) REFERENCES tours(tid),
    FOREIGN KEY (uid) REFERENCES users(uid)
);
-- 购票明细
CREATE TABLE tickets(
    tkid INT PRIMARY KEY AUTO_INCREMENT,
    -- 关联场次
    tid INT, 
    -- 关联用户
    uid INT, 
    time BIGINT,
    count INT,
    -- 支付状态：0 未支付 1 已支付
    status INT,
    FOREIGN KEY (tid) REFERENCES tours(tid),
    FOREIGN KEY (uid) REFERENCES users(uid)
);
